function scrollHistory(){scrollItems.forEach(function(e){const c=e.getBoundingClientRect().top;c<windowHeight?(e.classList.add("scroll-active"),circle.style.top=e.offsetTop+20+"px"):e.classList.remove("scroll-active"),c<30&&e.classList.remove("scroll-active")})}const circle=document.querySelector(".circle"),scrollItems=document.querySelectorAll(".scroll_item"),windowHeight=window.innerHeight/1.35;window.addEventListener("scroll",scrollHistory);